package dreamSchool.cscourse.chapter04;

public class CalculatorTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number1 = 20;
		int number2 = 7;
		
		int p = number1 + number2;
		int m = number1 - number2;
		int g = number1 * number2;
		int n = number1 / number2;
		int s = number1 % number2;
		
		System.out.println("옮 써쎀 : " + p);
		System.out.println("짋 써쎀 : " + m);
		System.out.println("썼 써쎀 : " + g);
		System.out.println("씱얋 써쎀 : " + n);
		System.out.println("씱쟵쵔 써쎀 : " + s);
		
	}

}
